export default function Select({options, value, onChange}){return <select value={value} onChange={e=>onChange(e.target.value)}>{options.map(o=>
  <option key={o.value} value={o.value}>{o.label}</option>
)}</select>}

export default function Modal({open,onClose,children}){
  if(!open) return null;
  return <div className='fixed inset-0 flex items-center justify-center bg-black bg-opacity-50'>
    <div className='bg-white p-4 rounded'>{children}<button onClick={onClose}>Close</button></div>
  </div>
}

export function AccordionItem({title,children}){
  const [open,setOpen]=React.useState(false);
  return <div>
    <button onClick={()=>setOpen(!open)}>{title}</button>
    {open&&<div>{children}</div>}
  </div>
}

export function Tabs({tabs}){
  const [i,setI]=React.useState(0);
  return <div>
    <nav>{tabs.map((t,idx)=><button key={idx} onClick={()=>setI(idx)}>{t.title}</button>)}</nav>
    <div>{tabs[i].content}</div>
  </div>
}

export function Pagination({page, total, onChange}){return <div className='flex gap-2'>{Array.from({length:total}).map((_,i)=>
  <button key={i} onClick={()=>onChange(i+1)} className={i+1===page?'font-bold':''}>{i+1}</button>
)}</div>}

export default function Breadcrumbs({items}){return <nav aria-label='Breadcrumb'>
  <ol className='flex gap-2'>{items.map((i,k)=><li key={k}>{i}</li>)}</ol>
</nav>}

import Highlight from 'prism-react-renderer';
export default function Syntax({code,lang}){
  return <Highlight code={code} language={lang}>{(props)=> <pre {...props}/>}</Highlight>
}

import re
def is_email(s):
  return bool(re.match(r'^[^@]+@[^@]+\.[^@]+$', s))

def is_url(s):
  return s.startswith('http')

